<script lang="ts">
  import cn from "classnames";
  import { onMount } from "svelte";

  const { class: clazz = "w-full" } = $props();

  const viewBox = [-8, -8, 254, 87];

  let isHovered = $state(false);

  let groupsData = $state<
    Record<string, { ref: SVGGElement | null; dx: number; dy: number }>
  >({});

  let svgRef: SVGSVGElement | null = null;
  let svgCenter = $state({ x: 0, y: 0 });

  onMount(() => {
    calculateRadialVectors();
  });

  function calculateRadialVectors() {
    if (!svgRef) return;

    const [vx, vy, vw, vh] = viewBox;

    svgCenter.x = vx + vw / 2;
    svgCenter.y = vy + vh / 2;

    const groups = Array.from(svgRef.querySelectorAll("g:not(#heart)"));

    groupsData = Object.fromEntries(
      groups.map((group) => {
        const bbox = (group as SVGGElement).getBBox();

        const groupCenterX = bbox.x + bbox.width / 2;
        const groupCenterY = bbox.y + bbox.height / 2;

        const dx = groupCenterX - svgCenter.x;
        const dy = groupCenterY - svgCenter.y;

        return [
          group.id,
          {
            ref: group as SVGGElement,
            dx: dx * 0.125,
            dy: dy * 0.125,
          },
        ];
      }),
    );
  }

  function handleMouseEnter() {
    isHovered = true;
  }

  function handleMouseLeave() {
    isHovered = false;
  }
  const groupStyle = $derived.by(
    () => (id: string) =>
      isHovered
        ? `transform: translate(${groupsData[id]?.dx ?? 0}px, ${groupsData[id]?.dy ?? 0}px);`
        : "",
  );
</script>

